package cn.adwalker.core.thread;import java.util.Date;import org.springframework.context.ApplicationContext;import cn.adwalker.core.spring.AppContext;import cn.adwalker.ad.admin.task.service.IDevIncomeTaskService;/** *  * <p> * Title: ClearCacheTask * </p> * <p> * Description:TODO * </p> * <p> * Company: adwalker * </p> *  * @author cuidd * @date 2013-8-2 */public class DevEffectTask implements Runnable {	private Date date;	public DevEffectTask(Date date) {		this.date = date;	}	public void run() {		while (true) {			ApplicationContext ctx = AppContext.getApplicationContext();			IDevIncomeTaskService service = (IDevIncomeTaskService) ctx					.getBean(IDevIncomeTaskService.class);			try {				boolean b = service.tranceDevEffect(date);				if (b) {					break;				}			} catch (Exception e) {				e.printStackTrace();			}		}	}}