package cn.adwalker.core.thread;import cn.adwalker.core.util.HttpClientUtil;/** *  * <p> * Title: ApiSendTask * </p> * <p> * Description:TODO * </p> * <p> * Company: adwalker * </p> *  * @author cuidd * @date 2013-8-2 */public class ApiSendTask implements Runnable {	private String params;	private String url;	/**	 * 	 * <p>	 * Title:	 * </p>	 * <p>	 * Description:TODO	 * </p>	 * 	 * @param url	 * @param params	 */	public ApiSendTask(String url, String params) {		this.params = params;		this.url = url;	}	/**	 * (non-Javadoc)	 * <p>	 * Title: run	 * </p>	 * <p>	 * Description:TODO	 * </p>	 * 	 * @see java.lang.Runnable#run()	 */	public void run() {		try {			Thread.sleep(10000);		} catch (InterruptedException e) {			e.printStackTrace();		}		HttpClientUtil.httpRequestPost(url, params);	}}