package cn.adwalker.ad.core.pool;import java.util.concurrent.ArrayBlockingQueue;import java.util.concurrent.ThreadPoolExecutor;import java.util.concurrent.TimeUnit;public class ThreadPoolUtil {		private static ThreadPoolExecutor threadPool;	private ThreadPoolUtil() {			}	public static ThreadPoolExecutor getThreadPool() {		if (threadPool == null) {			System.out.println("threadPool is init...");			threadPool = new ThreadPoolExecutor(1, 3, 3, TimeUnit.SECONDS, new ArrayBlockingQueue<Runnable>(3), new ThreadPoolExecutor.CallerRunsPolicy());		}		return threadPool;	}	}