package cn.adwalker.ad.control.pool;import java.util.concurrent.ArrayBlockingQueue;import java.util.concurrent.ThreadPoolExecutor;import java.util.concurrent.TimeUnit;/** * 线程池工具类 *  * @author jiangwei * @date Sep 26, 2012 */public class ThreadPoolUtil {		private static ThreadPoolExecutor threadPool;	private ThreadPoolUtil() {			}	public static ThreadPoolExecutor getThreadPool() {		if (threadPool == null) {			threadPool = new ThreadPoolExecutor(1, 3, 3, TimeUnit.SECONDS, new ArrayBlockingQueue<Runnable>(3), new ThreadPoolExecutor.CallerRunsPolicy());		}		return threadPool;	}	}